SELECT id, username, password, salt, email, type, status, activation_code, header_url, create_time
FROM user
WHERE id=${id}

SELECT id, username, password, salt, email, type, status, activation_code, header_url, create_time
FROM user
WHERE username=${username}

SELECT id, username, password, salt, email, type, status, activation_code, header_url, create_time
FROM user
WHERE email=${email}
